from PIL import Imageimport mathimport osdef getMinImageSize(imageFilepaths):    sizes = []    for fp in imageFilepaths:        sizes.append(getSize(fp))            minWidth = min(sizes, key=lambda e: e[0])[0]    minHeight = min(sizes, key=lambda e: e[1])[1]            return minWidth, minHeightdef displayMinImageSize(imageFilepaths):    minWidth, minHeight = getMinImageSize(uncroppedImagePaths)    print(f"min width: {minWidth}")    print(f"min height: {minHeight}")    def cropImage(filePath, size):    halfWidth = size[0] / 2    halfHeight = size[1] / 2    with Image.open(filePath) as img:        cx, cy = (img.size[0] // 2, img.size[1] // 2)        # (l, t, r, b)        cropBox = (math.floor(cx-halfWidth), math.floor(cy-halfHeight), math.floor(cx+halfWidth), math.floor(cy+halfHeight))        # Do cropping magic        croppedImg = img.crop(cropBox)    # return cropped image    return croppedImg        def getSize(filePath):    with Image.open(filePath) as img:        size = img.size            return sizedef main():    fileNames = [filename for filename in os.listdir("./uncropped")]    uncroppedImagePaths = [f"uncropped/{filename}" for filename in fileNames]    croppedImagePaths = [f"cropped/{filename}" for filename in fileNames]    croppingDimensions = (400, 400)    for filename, inputPath, outputPath in zip(fileNames, uncroppedImagePaths, croppedImagePaths):        print(f"Cropping img '{filename}' to size {croppingDimensions}. ", end="")        croppedImg = cropImage(inputPath, croppingDimensions)        # use img.save(filepath) to save an image        croppedImg.save(outputPath)        print(f"Save successful")        croppedImg.close()                # print(f"I got an image that's {croppedImg.size}")if __name__ == "__main__":    main()